import SearchIcon from '@material-ui/icons/Search';
import "./WeatherApp.css";
import WeatherType from './WeatherType';


function WeatherApp(props) {
    const[weatherForcast,setweatherForcast] = useState();

      useEffect(() => {
        fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=34.025917&lon=71.560135&exclude=hourly,minutely&appid=fc4f7b08db5ab23438b8c846324420d6`)
        .then(res => res.json())
        .then(res => setweatherForcast(res.daily.map(df => {
            return{
                min: df.temp.min,
                max: df.temp.max,
            }
        })))



    }, [])


    const WeatherInfo = () => {
        try {
           fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=34.025917&lon=71.560135&exclude=hourly,minutely&appid=fc4f7b08db5ab23438b8c846324420d6`)
         .then (res => res.json())
         .then(res => setweatherForcast(res.daily.map(df => {
             return{
                 min: df.temp.min,
                 max: df.temp.max,
            }
       })))

          console.log(weatherForcast);

        } catch (error) {

        }

    }

    useEffect(() => {
        WeatherInfo();
    }, [])



    return (
        <>
           <div className="hearder">
           <div className="search-bar">
            <SearchIcon  className="search-Icon"/>
           <input
            type="search"
            placeholder="search..."
            autoFocus
            id="search"
            className="search"
         />  
               </div> 
               <div className="button-div">
               <button onClick={WeatherInfo} className="button" >Search</button>
               </div>
               </div>



              <div key={index}>{weatherForcast}</div>



        </>
    )
}

export default WeatherApp
